		<th><?=@_order_by?></th>					<th><?=@_action?></th>					<th><input type="checkbox" name="chkall" onclick="chkallClick(this)"></th>				</tr>			</thead>			<tbody>				<?php if(!empty($result)) { ?>					<?php $i = 0; foreach($result as $items=>$item) { $i++;?>					<tr>						<td><?=$i?></td>						<td>&rarr; <?=$item->name?></td>						<td><?=$item->links?></td>						<td><?php if($item->status == 1) { ?><i style="cursor: pointer; color: green;" onclick="updateStatus('<?=$item->id?>','status')" class="fa fa-check"></i><?php } else { ?><i style="cursor: pointer; color: red;" onclick="updateStatus('<?=$item->id?>','status')" class="fa fa-ban"></i><?php } ?></td>						<td><input type="text" autocomplete="off" name="order_by" id="order_by_<?=$item->id?>" class="form-control input-xsmall" value="<?=$item->order_by?>" onkeyup="changeOrderBy(<?=$item->id?>,this.value)" ></td>						<td>							<a href="<?=PATH_URL?>acp/categories/edit/<?=$item->id?>" class="tooltips yellow btn btn-xs " data-container="body" data-placement="top" data-original-title="<?=@_edit?>"><i class="fa fa-pencil"></i>  <?=@_edit?> </a>							<a href="<?=PATH_URL?>acp/categories/del/<?=$item->id?>" class="tooltips red btn btn-xs btn_delete_single" data-container="body" data-placement="top" data-original-title="<?=@_delete?>"><i class="fa fa-trash-o"></i> <?=@_delete?> </a>						</td>						<td><input type="checkbox" name="id[]" value="<?=$item->id?>"></td>					</tr>					<?php $child = $this->model->getListContent('category', array('parent_id' => $item->id), "order_by asc", null, null, "name,links,id,status,order_by")?>					<?php if(!empty($child)) { ?>						<?php $j = 0; foreach($child as $items1=>$item1) { $j++;?>							<tr>								<td><?=$i.$j?></td>								<td>&rarr;&rarr; <?=$item1->name?></td>								<td><?=$item1->links?></td>								<td><?php if($item1->status == 1) { ?><i style="cursor: pointer; color: green;" onclick="updateStatus('<?=$item1->id?>','status')" class="fa fa-check"></i><?php } else { ?><i style="cursor: pointer; color: red;" onclick="updateStatus('<?=$item1->id?>','status')" class="fa fa-ban"></i><?php } ?></td>								<td><input type="text" autocomplete="off" name="order_by" id="order_by_<?=$item1->id?>" class="form-control input-xsmall" value="<?=$item1->order_by?>" onkeyup="changeOrderBy(<?=$item1->id?>,this.value)" ></td>								<td>									<a href="<?=PATH_URL?>acp/categories/edit/<?=$item1->id?>" class="tooltips yellow btn btn-xs " data-container="body" data-placement="top" data-original-title="<?=@_edit?>"><i class="fa fa-pencil"></i>  <?=@_edit?> </a>									<a href="<?=PATH_URL?>acp/categories/del/<?=$item1->id?>" class="tooltips red btn btn-xs btn_delete_single" data-container="body" data-placement="top" data-original-title="<?=@_delete?>"><i class="fa fa-trash-o"></i> <?=@_delete?> </a>								</td>								<td><input type="checkbox" name="id[]" value="<?=$item1->id?>"></td>							</tr>						<?php } ?>					<?php } ?>					<?php } ?>				<?php } ?>			</tbody>		</table>		<div>			<div class="pull-left" style="margin: 5px 0;">				<label><?=@_with_selected?>: </label>				<button type="submit" name="orderButton" class="btn btn-sm blue"><?=@_re_order?></button>				<button type="submit" name="hideButton" class="btn btn-sm yellow"><i class="fa fa-ban"></i> <?=@_deactive?></button>				<button type="submit" name="delButton" onclick="return confirm('Are you sure delete?')" class="btn btn-sm red btn_delete_multi"><i class="fa fa-trash-o"></i> <?=@_delete?></button>			</div>			<div class="pull-right"><?php if(!empty($pageindex)) echo $pageindex;?></div>			<div class="clearfix"></div>		</div>	</form></div><script>	function deleteAll() {		var r = confirm('Are you sure !!!');		if(r == true) {			$("#frm_content").submit();		}	}	function updateStatus(id,type) {		$.post('<?=PATH_URL?>acp/categories/updateStatus',{id:id,type:type}, function(data){if(data == 1) location.reload();})	}	function changeOrderBy(id,val){		$.post('<?=PATH_URL?>acp/categories/changeOrderBy',{id:id, order_by:val},function(data){$("#order_by_"+id).val(data)})	}</script>