<h3 class="page-title"><?=@_order?> <small><?=@_all?> <?=@_order?></small></h3><div class="page-bar">	<ul class="page-breadcrumb">		<li>			<i class="fa fa-home"></i>			<a href="<?=PATH_URL?>acp"><?=@_home?></a>			<i class="fa fa-angle-right"></i>		</li>		<li>			<a href="#"><?=@_order?></a>		</li>	</ul></div><div class="panel-body">	<form id="frm_search" class="form-inline" method="post" action="<?=PATH_URL?>acp/order/view" style="margin-bottom: 20px;">		<div class="form-group">			<label><?=@_view?></label>			<select class="form-control" name="sort_by" id="sort_by" onchange="this.form.submit()">				<option value="15">15</option>				<option value="30">30</option>				<option value="50">50</option>				<option value="0"><?=@_all?></option>			</select>		</div>	</form>	<form name="frmList" id="frm_content" method="post" action="<?=PATH_URL?>acp/order/del" class="form-horizontal" enctype="multipart/form-data">		<?php if($this->session->flashdata('success') == 1) echo '<p style="text-align: center; color: red;">Success !!!</p>'?>		<table class="table table-striped table-bordered table-advance table-hover">			<thead class="flip-content">				<tr>					<th>#</th>					<th style="min-width: 250px;"><?=@_full_name?></th>					<th><?=@_email?></th>					<th><?=@_phone?></th>					<th><?=@_status?></th>					<th style="min-width: 150px;"><?=@_action?></th>					<th><input type="checkbox" name="chkall" onclick="chkallClick(this)"></th>				</tr>			</thead>			<tbody>				<?php if(!empty($result)) { ?>					<?php $i = 0; foreach($result as $items=>$item) { $i++; $member = $this->model->getContent('member', array('id' => $item->member_id), "fullname, email, phone")?>						<tr>							<td><?=$i?></td>							<td><a href="<?=PATH_URL?>acp/order/view/<?=$item->id?>"><?=$member->fullname?></a></td>							<td><?=$member->email?></td>							<td><?=$member->phone?></td>							<td>								<select class="table-group-action-input form-control input-inline input-small input-sm">									<option value="0" <?php if($item->status == 0) echo "selected";?>>Waiting..</option>									<option value="1" <?php if($item->status == 1) echo "selected";?>>Pending</option>									<option value="2" <?php if($item->status == 2) echo "selected";?>>Shipping</option>									<option value="3" <?php if($item->status == 3) echo "selected";?>>Complete</option>									<option value="4" <?php if($item->status == 4) echo "selected";?>>Canceled</option>								</select>							</td>							<td>								<a href="<?=PATH_URL?>acp/order/view/<?=$item->id?>" class="tooltips yellow btn btn-xs " data-container="body" data-placement="top" data-original-title="<?=@_edit?>"><i class="fa fa-eye"></i>  <?=@_edit?> </a>								<a href="<?=PATH_URL?>acp/order/del/<?=$item->id?>" class="tooltips red btn btn-xs btn_delete_single" data-container="body" data-placement="top" data-original-title="<?=@_delete?>"><i class="fa fa-trash-o"></i> <?=@_delete?> </a>							</td>							<td><input type="checkbox" name="id[]" value="<?=$item->id?>"></td>						</tr>					<?php } ?>				<?php } ?>			</tbody>		</table>		<div>			<div class="pull-left" style="margin: 5px 0;">				<label><?=@_with_selected?>: </label>				<button type="submit" name="delButton" onclick="return confirm('Are you sure delete?')" class="btn btn-sm red btn_delete_multi"><i class="fa fa-trash-o"></i> <?=@_delete?></button>				<button type="submit" name="exportXls" class="btn btn-sm green btn_delete_multi"><i class="fa fa-download"></i> <?=@_export_xls?></button>			</div>			<div class="pull-right"><?php if(!empty($pageindex)) echo $pageindex;?></div>			<div class="clearfix"></div>		</div>	</form></div><script>	function deleteAll() {		var r = confirm('Are you sure !!!');		if(r == true) {			$("#frm_content").submit();		}	}	function updateStatus(id,type) {		$.post('<?=PATH_URL?>acp/order/updateStatus',{id:id,type:type}, function(data){if(data == 1) location.reload();})	}	function changeOrderBy(id,val){		$.post('<?=PATH_URL?>acp/order/changeOrderBy',{id:id, order_by:val},function(data){$("#order_by_"+id).val(data)})	}</script>