<h3 class="page-title"><?=@_product?> <small><?=@_all?> <?=@_product?></small></h3><div class="page-bar">	<ul class="page-breadcrumb">		<li>			<i class="fa fa-home"></i>			<a href="<?=PATH_URL?>acp"><?=@_home?></a>			<i class="fa fa-angle-right"></i>		</li>		<li>			<a href="#"><?=@_product?></a>		</li>	</ul></div><div class="panel-body">	<form id="frm_search" class="form-inline" method="post" action="<?=PATH_URL?>acp/products/search" style="margin-bottom: 20px;">		<!--<div class="form-group">			<label><?=@_view?> </label>			<select class="form-control" name="sort_by" id="sort_by" onchange="this.form.submit()">				<option value="15">15</option>				<option value="30">30</option>				<option value="50">50</option>				<option value="0"><?=@_all?></option>			</select>		</div>-->		<div class="form-group">			<label class="sr-only" for="keywords"><?=@_keywords?></label>			<input type="text" class="form-control input-large" id="keywords" name="keywords" placeholder="<?=@_keywords?>">		</div>		<div class="form-group">			<select class="form-control input-large" name="category">				<option value="">&rarr; <?=@_category?> &larr;</option>				<?php if(!empty($category)) { ?>					<?php foreach($category as $items=>$item) { ?>						<?php if(!empty($result)) { ?>							<?php if($result->parent_id == $item->id) { ?>								<option value="<?=$item->id?>" selected><?=$item->name?></option>							<?php } else { ?>								<option value="<?=$item->id?>"><?=$item->name?></option>							<?php } ?>						<?php } else { ?>							<option value="<?=$item->id?>"><?=$item->name?></option>						<?php } ?>					<?php } ?>				<?php } ?>			</select>		</div>		<div class="form-group">			<button type="submit" id="btn_search" class="btn btn-info"><i class="icon-search"></i> <?=@_search?></button>		</div>		<div class="form-group navbar-right" style="margin-right: 0;">			<button id="btn_submit" type="button" class="btn btn-success" onclick="return location.href='<?=PATH_URL?>acp/products/add'"><i class="icon-plus"></i> <?=@_add_new?></button>		</div>	</form>	<form name="frmList" id="frm_content" method="post" action="<?=PATH_URL?>acp/products/del" class="form-horizontal" enctype="multipart/form-data">		<?php if($this->session->flashdata('success') == 1) echo '<p style="text-align: center; color: red;">Success !!!</p>'?>		<table class="table table-striped table-bordered table-advance table-hover">			<thead class="flip-content">				<tr>					<th>#</th>					<th><?=@_image?></th>					<th style="min-width: 250px;"><?=@_name?></th>					<th><?=@_slug?></th>					<th style="min-width: 70px;"><?=@_price?></th>					<th style="min-width: 70px;"><i class="fa fa-star"></i> <i class="fa fa-home"></i> <i class="fa fa-check"></i></th>					<th><?=@_order_by?></th>					<th style="min-width: 100px;"><?=@_action?></th>					<th><input type="checkbox" name="chkall" onclick="chkallClick(this)"></th>				</tr>			</thead>			<tbody>				<?php if(!empty($result)) { ?>					<?php 						$i = 0; foreach($result as $items=>$item) { $i++;						$products = $this->model->getContent('products', array('article_id' => $item->id),"code,price");					?>						<tr>							<td><?=$i?></td>							<td><a href="<?=PATH_URL?>acp/products/edit/<?=$item->id?>"><img src="<?=PATH_URL?>img_data/<?php if($item->images <> null) echo $item->images; else echo "no_images.gif";?>" class="img-thumbnail" style="max-width: 100px;"></a></td>							<td><?=$item->name?><br><?=$products->code?></td>							<td><?=$item->links?></td>							<td><?=number_format($products->price,0,'.',',')?></td>							<td>								<?php if($item->featured == 1) { ?><i style="cursor: pointer; color: green;" onclick="updateStatus('<?=$item->id?>','featured')" class="fa fa-star-o"></i><?php } else { ?><i style="cursor: pointer; color: gray;" onclick="updateStatus('<?=$item->id?>','featured')" class="fa fa-star-o"></i><?php } ?>								<?php if($item->homepage == 1) { ?><i style="cursor: pointer; color: green;" onclick="updateStatus('<?=$item->id?>','homepage')" class="fa fa-home"></i><?php } else { ?><i style="cursor: pointer; color: gray;" onclick="updateStatus('<?=$item->id?>','homepage')" class="fa fa-home"></i><?php } ?>								<?php if($item->status == 1) { ?><i style="cursor: pointer; color: green;" onclick="updateStatus('<?=$item->id?>','status')" class="fa fa-check"></i><?php } else { ?><i style="cursor: pointer; color: red;" onclick="updateStatus('<?=$item->id?>','status')" class="fa fa-ban"></i><?php } ?>							</td>							<td><input type="text" autocomplete="off" name="order_by[]" id="order_by_<?=$item->id?>" class="form-control input-xsmall" value="<?=$item->order_by?>" onkeyup="changeOrderBy(<?=$item->id?>,this.value)" ></td>							<td>								<a href="<?=PATH_URL?>acp/products/edit/<?=$item->id?>" class="tooltips yellow btn btn-xs " data-container="body" data-placement="top" data-original-title="<?=@_edit?>"><i class="fa fa-pencil"></i>  <?=@_edit?> </a>								<a href="<?=PATH_URL?>acp/products/del/<?=$item->id?>" class="tooltips red btn btn-xs btn_delete_single" data-container="body" data-placement="top" data-original-title="<?=@_delete?>"><i class="fa fa-trash-o"></i> <?=@_delete?> </a>							</td>							<td><input type="checkbox" name="id[]" value="<?=$item->id?>"></td>						</tr>					<?php } ?>				<?php } ?>			</tbody>		</table>		<div>			<div class="pull-left" style="margin: 5px 0;">				<label><?=@_with_selected?>: </label>				<button type="submit" name="orderButton" class="btn btn-sm blue"><?=@_re_order?></button>				<button type="submit" name="hideButton" class="btn btn-sm yellow"><i class="fa fa-ban"></i> <?=@_deactive?></button>				<button type="submit" name="delButton" onclick="return confirm('Are you sure delete?')" class="btn btn-sm red btn_delete_multi"><i class="fa fa-trash-o"></i> <?=@_delete?></button>			</div>			<div class="pull-right"><?php if(!empty($pageindex)) echo $pageindex;?></div>			<div class="clearfix"></div>		</div>			</form>	</div><script>	function deleteAll() {		var r = confirm('Are you sure !!!');		if(r == true) {			$("#frm_content").submit();		}	}	function updateStatus(id,type) {		$.post('<?=PATH_URL?>acp/products/updateStatus',{id:id,type:type}, function(data){if(data == 1) location.reload();})	}	function changeOrderBy(id,val){		$.post('<?=PATH_URL?>acp/products/changeOrderBy',{id:id, order_by:val},function(data){$("#order_by_"+id).val(data)})	}</script>