orm-body">								<div class="form-group">									<label class="control-label col-md-3"><?=@_groups?></label>									<div class="col-md-6">										<select class="form-control select2me" name="groups" required data-placeholder="Select...">											<option value="editor" <?php if(!empty($result)) { if($result->groups == 'editor') echo "selected"; }?>>Editor</option>											<option value="managers" <?php if(!empty($result)) { if($result->groups == 'managers') echo "selected"; }?>>Managers</option>											<option value="administrators" <?php if(!empty($result)) { if($result->groups == 'administrators') echo "selected"; }?>>Administrators</option>										</select>									</div>								</div>								<div class="form-group">									<label class="col-md-3 control-label"><?=@_username?></label>									<div class="col-md-6">										<div class="input-icon">											<i class="fa fa-user"></i>											<input type="text" class="form-control" onkeyup="addLinks(this.value)" autocomplete="false" name="txtUsername" id="txtUsername" <?php if(!empty($result)) { echo " readonly"; }?> required maxlenght="32" value="<?=$result?$result->username:""?>" placeholder="<?=@_username?>">											<span class="help-block">											<?=@_username_help_block?>. </span>										</div>									</div>								</div>								<div class="form-group">									<label class="col-md-3 control-label"><?=@_password?></label>									<div class="col-md-6">										<div class="input-group input-icon">											<i class="fa fa-lock fa-fw"></i>											<input type="password" autocomplete="false" class="form-control hide_password" name="txtPassword" requeried placeholder="<?=@_password?>">											<span id="show_hide_pass" class="input-group-addon" style="cursor:pointer;"><i class="fa fa-eye"></i> Show</span>										</div>										<button type="button" class="btn btn-default" id="generate_password" style="margin-top:10px;">Generate password</button>									</div>								</div>				